import React from 'react';
import { Link } from 'react-router-dom';

const AuthorList = ({ authors, deleteAuthor }) => {
  return (
    <div>
      <h2>We have quotes by:</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Author</th>
            <th scope="col">Actions Available</th>
          </tr>
        </thead>
        <tbody>
          {authors.map((author) => (
            <tr key={author._id}>
              <td>{author.name}</td>
              <td>
                <Link className="btn btn-primary me-2" to={`/edit/${author._id}`}>Edit</Link>
                <button className="btn btn-danger" onClick={() => deleteAuthor(author._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* <Link className="btn btn-success" to="/create">Add an Author</Link> */}
    </div>
  );
};

export default AuthorList;
