import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EditAuthor = (props) => {
    const [errors, setErrors] = useState([]);
    const [author, setAuthor] = useState({});
    const navigate = useNavigate();
    const { id } = useParams(); 

    useEffect(() => {
        axios.get('http://localhost:8000/api/authors/' + props.id)
            .then(res => setAuthor(res.data))
    }, [props.id]);

    const updateAuthor = e => {
        e.preventDefault();
        axios.put('http://localhost:8000/api/authors/' + props.id, author)
            .then(res => {
                if(res.data.errors) {
                    setErrors(res.data.errors);
                } else {
                    navigate("/");
                }
            })
    }

    const handleInputChange = (e) => {
        setAuthor({
            ...author,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className="container">
            <h2>Edit Author:</h2>
            {errors.map((err, index) => <p key={index}>{err}</p>)}
            <form onSubmit={updateAuthor}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" className="form-control" onChange={handleInputChange} name="name" value={author.name}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
                <button onClick={() => navigate("/")} className="btn btn-primary">Cancel</button>
            </form>
        </div>
    )
}

export default EditAuthor;
